mapper.select:
SELECT * FROM book_view
and title like '%${query}%'
and category_id = ${categoryId}

select * from ( select b.id, b.category_id, b.title, b.author, b.pub_date, b.publisher, b.isbn13, b.description, b.purchase_link, b.price, b.cover, b.public_yn, b.reg_date, c.name, count(bs.id) best_count from book b left join book_category c on b.category_id = c.id left join bestseller bs on b.id = bs.book_id group by b.id order by b.reg_date desc ) v
${params.queryType} like '%${params.query}%'
and category_id = #{params.categoryId}
order by id desc LIMIT #{offset}, #{size}

select count(id) count from book_view
${params.queryType} like '%${params.query}%'
and category_id = #{params.categoryId}
